private void vObrigacaoXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (string.IsNullOrEmpty(vObrigacaoXrLabel.Text)) 
	{
		vObrigacaoXrLabel.Text = "";
	}
	else
	{
		vObrigacaoXrLabel.Text = vObrigacaoXrLabel.Text.Substring(0, 1) + "-" + vObrigacaoXrLabel.Text.Substring(1, 5);
	}
}


private void vSistemaXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (string.IsNullOrEmpty(vSistemaXrLabel.Text)) 
	{
		vSistemaXrLabel.Text = "";
	}
	else if (vSistemaXrLabel.Text == "CON") 
	{
		vSistemaXrLabel.Text = "Buddywin Contábil";
	} 	
	else if (vSistemaXrLabel.Text == "CAD") 
	{
		vSistemaXrLabel.Text = "Buddywin Cadastro Geral";
	} 
	else if (vSistemaXrLabel.Text == "DEC") 
	{
		vSistemaXrLabel.Text = "Buddywin Declara";
	} 
	else if (vSistemaXrLabel.Text == "ESC") 
	{
		vSistemaXrLabel.Text = "Buddywin Escrita Fiscal";
	} 
	else if (vSistemaXrLabel.Text == "FOL") 
	{
		vSistemaXrLabel.Text = "Buddywin Folha de Pagamento";
	} 
	else if (vSistemaXrLabel.Text == "LAL") 
	{
		vSistemaXrLabel.Text = "Buddywin Lalur";
	} 
}


