private void vCompetenciaObsoletaXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (!string.IsNullOrEmpty(vCompetenciaObsoletaXrLabel.Text)) 
	{
		vCompetenciaObsoletaXrLabel.Text = vCompetenciaObsoletaXrLabel.Text.Substring(0, 2) + "/" + vCompetenciaObsoletaXrLabel.Text.Substring(2, 4);
	}
}

private void vSubsequenteXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (string.IsNullOrEmpty(vSubsequenteXrLabel.Text))
	{
		vSubsequenteXrLabel.Text = "";
	}
	else if (Convert.ToInt32(vSubsequenteXrLabel.Text) == 0)
	{
		vSubsequenteXrLabel.Text = "";
	}
	else
	{
		vSubsequenteXrLabel.Text = vSubsequenteXrLabel.Text;
	}
}

