private void vTipoXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (string.IsNullOrEmpty(vTipoXrLabel.Text))
	{
		detalheGroupHeader.Visible = false;
	}
	else if (vTipoXrLabel.Text == "I") 
	{
		detalheGroupHeader.Visible = false;
		vvarcodXrLabel.Visible = false;
		vvardetXrLabel.Visible = false;
	} 
	else
	{
		detalheGroupHeader.Visible = true;
		vvarcodXrLabel.Text = "";
		vvarcodXrLabel.Visible = true;
		if (vTipoXrLabel.Text == "C") 
		{
			cvarcodXrLabel.Text = "CPR: " + vvarcodXrLabel.Text;
			vvardetXrLabel.Visible = false;
		} 
		else if ((vTipoXrLabel.Text == "S") | (vTipoXrLabel.Text == "E"))
		{
			cvarcodXrLabel.Text = "Município:";
			vvardetXrLabel.Visible = true;
		} 
		else if (vTipoXrLabel.Text == "N") 
		{
			cvarcodXrLabel.Text = "CNPJ - Oitavo Dígito:";
			vvardetXrLabel.Visible = false;
		}
		else if (vTipoXrLabel.Text == "T") 
		{
			cvarcodXrLabel.Text = "IE - Último Dígito:";
			vvardetXrLabel.Visible = false;
		}
	}
}


private void vTipoDiaXrLabel_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e) 
{
	if (string.IsNullOrEmpty(vTipoDiaXrLabel.Text))
	{
		vTipoDiaXrLabel.Text = "Dia Fixo";
	}
	else if (vTipoDiaXrLabel.Text == "F") 
	{
		vTipoDiaXrLabel.Text = "Dia Fixo";
	}
	else if (vTipoDiaXrLabel.Text == "U") 
	{
		vTipoDiaXrLabel.Text = "Dia Útil";
	}
}
